import React, {useState, useEffect} from 'react'

function StarWars(){
    const [state, setState] = useState()
    const [movie, setMovie] = useState("")
    const getDetails=(event)=>{
        setState(event.target.value)
    }
    useEffect(()=>{
        async function getData() {
            const res = await fetch(`https://swapi.dev/api/films/${state}/`).then(data=>data.json())
            setMovie(res)
        }
        getData();
    }, [state])
    return(
        <div>
            <h2> Star Wars Movie Details</h2>
            <div>{state === 'Select' ? 'Please select one movie' : "" }</div>
            <select onChange={getDetails}>
                <option value="Select">Select a Movie</option>
                <option value='1'>1</option>
                <option value='2'>2</option>
                <option value='3'>3</option>
                <option value='4'>4</option>
                <option value='5'>5</option>
                <option value='6'>6</option>
            </select>
            <h1>Title: {movie.title}</h1>
            <h4>Episode: {movie.episode_id}</h4>
            <p>Desp: {movie.opening_crawl}</p>
        </div>
    )
}
export default StarWars
