import React, {useState, useEffect} from 'react'

function StarWars(){
    const [state, setState] = useState()
    const getDetails=(event)=>{
        setState(event.target.value)
    }
    useEffect(()=>{
        async function getData() {
            const res = await fetch("https://swapi.dev/api/films/2/").then(data=>data.json()).then(data=>console.log(data))
        }
        getData();
    })
    return(
        <div>
            <h2> Star Wars Movie Details</h2>
            <div>{state === 'Select' ? 'Please select one movie' : state }</div>
            <select onChange={getDetails}>
                <option value="Select">Select a Movie</option>
                <option value='1'>1</option>
                <option value='2'>2</option>
                <option value='3'>3</option>
                <option value='4'>4</option>
                <option value='5'>5</option>
                <option value='6'>6</option>
                <option value='7'>7</option>
            </select>
        </div>
    )
}
export default StarWars
