        const {name, value} = event.target;
        setLastName(value)
    }

    const getDetailsFirst = (event) => {
        const {name, value} = event.target;
        setFirstName(value)
    }
    return(
            <div>
                <section>
                    <h2>Entered Name is: {firstName} {lastName}</h2>
                    <label>Enter first name</label>
                    <input 
                        type="text"
                        value={firstName}
                        name="firstName"
                        onChange={setFirstName}
                    />
                </section>
                <section>
                    <label>Enter last name</label>
                    <input 
                        type="text"
                        value={lastName}
                        name="lastName"
                        onChange={setLastName}
                    />
                </section>
            </div>
    )
}

