function SimpleForm(){
    const [firstName, setFirstName] = useValue()
    const [lastName, setLastName] = useValue() 
    const [email, setEmail] = useValue() 

    return(
            <div>
                <section>
                    <h2>Entered Name is: {firstName} {lastName}</h2>
                    <label>Enter first name</label>
                    <input 
                        type="text"
                        value={firstName}
                        name="firstName"
                        onChange={setFirstName}
                    />
                </section>
                <section>
                    <label>Enter last name</label>
                    <input 
                        type="text"
                        value={lastName}
                        name="lastName"
                        onChange={setLastName}
                    />
                </section>
                <section>
                    <label>Enter Email ID</label>
                    <input 
                        type="text"
                        value={email}
                        name="email"
                        onChange={setEmail}
                    />
                </section>
            </div>
    )
}

export default SimpleForm
