import React, {useState} from 'react'
import useToggle from './useToggle'

function Counter() {
    const [count, setCount] = useState(0);
    const [text, setTextToggle] = useToggle(true);
    const [color, setColorToggle] = useToggle(true);
    const [present, setPresentToggle] = useToggle(true);

    const addOne = () => {
        setCount(count+1)
    }
    return(
        <div>
            <section>
                <h2>Hooks Example 1: Add One</h2>
                <span>Counter Count: {count}</span><br/>
                <button onClick={addOne}>Up the game !!</button>
            </section>
            <section>
                <h2>Hooks Example 2: Toggle</h2>
                <button onClick={setTextToggle}>
                    {text ? "Initial Value !!" : 'Clicked Value'}
                </button>
            </section>
            <section>
                <h2>Hooks Example 3: Value II</h2>
                <button onClick={setColorToggle}>
                    {color ? "White !!" : 'Yellow ahh'}
                </button>
            </section>
        </div> 
    )
}

export default Counter
