function SimpleForm(){
    const [name, setName] = useState('TIAA')
    const handleChange = (event) =>{
        const {name, value} = event.target;
        setName(name = value)
    }
    return(
            <div>
                <h2>Entered Name is: {name}</h2>
                <label>Enter your name</label>
                <input 
                    type="text"
                    value={name}
                    name="name"
                    onChange={handleChange}
                />
            </div>
    )
}

export default SimpleForm
